import { useEffect, useState } from "react";
import { useNavigate } from "react-router";
import "./ProductList.css";

export default function ProductList() {
    const [products, setProducts] = useState([]); // Инициализация пустым массивом
    const navigate = useNavigate();

    useEffect(() => {
        fetch("http://127.0.0.1:8000/product-list")
            .then((response) => response.json())
            .then((data) => setProducts(data.products || [])) // ✅ Проверка на undefined
            .catch((error) => {
                console.error("Ошибка при загрузке продуктов:", error);
                setProducts([]); // ✅ Безопасная обработка ошибки
            });
    }, []);

    return (
        <div className="product-list__container">
            {localStorage.getItem('isLoggedIn') ? (
                <div className="add-product__container">
                    <button className="add-product__button">+ Добавить продукт</button>
                </div>
            ): ('')}
            {products && products.length === 0 ? ( // ✅ Проверка products
                <p className="no-products">Загруженных товаров не найдено :(</p>
            ) : (
                <div className="products__cards-inner">
                    {products.map((product) => (
                        <div
                            className="product__card"
                            key={product.id}
                            onClick={() => navigate(`/product/${product.id}`)}
                        >
                            <h3 className="product__card-name">{product.name}</h3>
                            <p className="product__card-price">Цена: {product.price} руб.</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}
