import { useState, useEffect } from 'react';
import './WhyThisCompany.css';
import whyReasons from './whyData';

export default function WhyThisCompany() {
    const [isEditing, setIsEditing] = useState(false);
    const [data, setData] = useState(whyReasons);
    const isLoggedIn = localStorage.getItem('isLoggedIn');

    // –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö
    const handleChange = (index, field, value) => {
        const updatedData = [...data];
        updatedData[index][field] = value;
        setData(updatedData);
    };

    // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ –¥–∞–Ω–Ω—ã—Ö –Ω–∞ —Å–µ—Ä–≤–µ—Ä
    const saveData = async () => {
        try {
            const response = await fetch('/api/why_this_company', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ title: "–ü–æ—á–µ–º—É –Ω–∞—Å –≤—ã–±–∏—Ä–∞—é—Ç?", blocks: data }),
            });

            if (response.ok) {
                alert('–î–∞–Ω–Ω—ã–µ —É—Å–ø–µ—à–Ω–æ —Å–æ—Ö—Ä–∞–Ω–µ–Ω—ã!');
                setIsEditing(false);
            } else {
                alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏');
            }
        } catch (error) {
            console.error('–û—à–∏–±–∫–∞:', error);
        }
    };

    return (
        <div className='why__container'>
            <h2 className='why__title'>–ü–æ—á–µ–º—É –Ω–∞—Å –≤—ã–±–∏—Ä–∞—é—Ç?</h2>

            {isLoggedIn && !isEditing && (
                <button className='why__edit-button' onClick={() => setIsEditing(true)}>‚úèÔ∏è –í–Ω–µ—Å—Ç–∏ –¥–∞–Ω–Ω—ã–µ</button>
            )}

            <div className='why__blocks-wrapper'>
                {data.map((reason, index) => (
                    <div className='why__block' key={reason.id}>
                        <div className='why__image-content'>
                            <img className='why__image' src={reason.image} alt={reason.alt} />
                        </div>
                        <div className='why__text-content'>
                            {isEditing ? (
                                <>
                                    <textarea className='why__block-title-edit' value={reason.title} onChange={(e) => handleChange(index, 'title', e.target.value)}/>
                                    <textarea className='why__block-text-edit' value={reason.text} onChange={(e) => handleChange(index, 'text', e.target.value)}/>
                                </>
                            ) : (
                                <>
                                    <h4 className='why__block-title'>{reason.title}</h4>
                                    <p className='why__block-text'>{reason.text}</p>
                                </>
                            )}
                        </div>
                        <div className='why__number-block'>{reason.id}</div>
                    </div>
                ))}
            </div>

            {isEditing && (
                <button className='why__save-button' onClick={saveData}>üíæ –°–æ—Ö—Ä–∞–Ω–∏—Ç—å</button>
            )}
        </div>
    );
}
