import './Vacancy.css';
import { useState } from 'react';

export default function Vacancy(){
    const [addVacancyModalOpen, setAddVacancyModalOpen] = useState(false);
    const [vacancyTitle, setVacancyTitle] = useState('');
    const [vacancySalary, setVacancySalary] = useState('');
    const [vacancyDescription, setVacancyDescription] = useState('');

    return (
        <div className='vacancy__container'>
            <h2 className='container__title'>Вакансии</h2>
            <button className='button__add--vacancy' style={{display: localStorage.getItem('isLoggedIn') ? 'block' : 'none'}} onClick={() => setAddVacancyModalOpen(true)}>Добавить вакансию</button>
            <div className='vacancy__block'>
                <h2 className='vacancy__title'>Стажёр-разработчик</h2>
                <p className='vacancy__salary'>50.000 - 80.000₽</p>
                <div className='vacancy__text'>
                    <p className='vacancy__text-item'>Требуется стажёр-разработчик фывофлд ыовлдфы олдво лдфыфыодва фолдывра олдфрыва рфыолда ролдфырва олдрфыв олдарфолывр оларфыволд ар</p>
                </div>
                <div className='vacancy__buttons-container'>
                    <button className='button--remove-vacancy' style={{display: localStorage.getItem('isLoggedIn') ? 'block' : 'none'}}>Удалить вакансию</button>
                    <button className='vacancy__button'>Откликнуться</button>
                </div>
            </div>
            <button className='vacancy__button--adaptive'>Показать ешё</button>

            {addVacancyModalOpen && (
                <div className="modal-overlay">
                    <div className="modal">
                        <h2>Добавить вакансию</h2>
                        <input type="text" placeholder="Название вакансии" onChange={(e) => setVacancyTitle(e)} />
                        <input type="number" placeholder="Зарплата (без руб)" onChange={(e) => setVacancyTitle(e)} />
                        <input type="text" placeholder="Полное описание" onChange={(e) => setVacancyDescription(e)} />
                        <button className="modal-button add">Добавить</button>
                        <button className="modal-button cancel">Отмена</button>
                    </div>
                </div>
            )}
        </div>
    );
}
