import { useState } from "react";
import "./AdminLogin.css";

export default function AdminLogin() {
    const [name, setName] = useState("");
    const [password, setPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [error, setError] = useState("");

    const handleLogin = async (e) => {
        e.preventDefault();
        setError("");

        try {
            const response = await fetch(`http://127.0.0.1:8000/login?name=admin&password=${password}`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
            });

            if (!response.ok) {
                throw new Error("Неверный логин или пароль");
            }

            setIsLoggedIn(true);
        } catch (err) {
            setError(err.message);
        }
    };

    const handleChangePassword = async (e) => {
        e.preventDefault();
        setError("");

        try {
            const response = await fetch(`http://127.0.0.1:8000/change_admin_password?name=admin&old_password=${password}&new_password=${newPassword}`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ name, old_password: password, new_password: newPassword }),
            });

            if (!response.ok) {
                throw new Error("Ошибка смены пароля");
            }

            alert("Пароль успешно изменён");
            setIsLoggedIn(false);
            setPassword("");
            setNewPassword("");
        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <div className="login__container">
            <form onSubmit={isLoggedIn ? handleChangePassword : handleLogin} className="login__form">
                <h2>{isLoggedIn ? "Сменить пароль" : "Вход для администратора"}</h2>

                {error && <p className="error">{error}</p>}

                <input
                    type="text"
                    placeholder="Логин"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                    disabled={isLoggedIn}
                />

                <input
                    type="password"
                    placeholder="Пароль"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />

                {isLoggedIn && (
                    <input
                        type="password"
                        placeholder="Новый пароль"
                        value={newPassword}
                        onChange={(e) => setNewPassword(e.target.value)}
                        required
                    />
                )}

                <button type="submit">{isLoggedIn ? "Сменить пароль" : "Войти"}</button>
            </form>
        </div>
    );
}
