import { useState, useEffect } from 'react';
import './WhyThisCompany.css';
import whyReasons from './whyData';
import InformationModal from '../InformationModal/InformationModal';

export default function WhyThisCompany() {
    const [isEditing, setIsEditing] = useState(false);
    const [data, setData] = useState([]);
    const [showInformationModal, setShowInformationModal] = useState(false);
    const [errorModal, setErrorModal] = useState(false);
    const [modalMessage, setModalMessage] = useState('');
    const isLoggedIn = localStorage.getItem('isLoggedIn');

    // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö —Å —Å–µ—Ä–≤–µ—Ä–∞
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('http://127.0.0.1:8000/why_this_company');
                if (response.ok) {
                    const result = await response.json();
                    if (result.blocks.length > 0) {
                        // –ó–∞–ø–æ–ª–Ω—è–µ–º –¥–∞–Ω–Ω—ã–µ –∫–∞—Ä—Ç–∏–Ω–∫–∞–º–∏ –∏–∑ –º–∞—Å—Å–∏–≤–∞, –µ—Å–ª–∏ –æ–Ω–∏ –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—Ç
                        const updatedData = result.blocks.map((block, index) => ({
                            ...block,
                            image: block.image || whyReasons[index].image,  // –ï—Å–ª–∏ –Ω–µ—Ç –∫–∞—Ä—Ç–∏–Ω–∫–∏ –Ω–∞ —Å–µ—Ä–≤–µ—Ä–µ, –±–µ—Ä–µ–º –∏–∑ whyReasons
                            alt: block.alt || whyReasons[index].alt,
                        }));
                        setData(updatedData);
                    } else {
                        setData(whyReasons); // –ï—Å–ª–∏ –¥–∞–Ω–Ω—ã—Ö –Ω–µ—Ç, –∏—Å–ø–æ–ª—å–∑—É–µ–º –¥–µ—Ñ–æ–ª—Ç–Ω—ã–µ
                    }
                } else {
                    setShowInformationModal(true);
                    setErrorModal(true);
                    setModalMessage('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –¥–∞–Ω–Ω—ã—Ö');
                    setData(whyReasons); // –í —Å–ª—É—á–∞–µ –æ—à–∏–±–∫–∏ –∑–∞–≥—Ä—É–∂–∞–µ–º –¥–µ—Ñ–æ–ª—Ç–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
                }
            } catch (error) {
                console.error('–û—à–∏–±–∫–∞:', error);
                setData(whyReasons); // –í —Å–ª—É—á–∞–µ –æ—à–∏–±–∫–∏ —Å–µ—Ç–∏ –∑–∞–≥—Ä—É–∂–∞–µ–º –¥–µ—Ñ–æ–ª—Ç–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
            }
        };

        fetchData();
    }, []);

    // –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö
    const handleChange = (index, field, value) => {
        const updatedData = [...data];
        updatedData[index][field] = value;
        setData(updatedData);
    };

    const saveData = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/why_this_company', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    title: "–ü–æ—á–µ–º—É –Ω–∞—Å –≤—ã–±–∏—Ä–∞—é—Ç?",
                    blocks: data.map(block => ({
                        ...block,
                        image: block.image || '/static/images/default-image.jpg',  // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –¥–µ—Ñ–æ–ª—Ç–Ω–æ–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ, –µ—Å–ª–∏ –Ω–µ—Ç
                    })),
                }),
            });
    
            if (response.ok) {
                setShowInformationModal(true);
                setErrorModal(false);
                setModalMessage('–î–∞–Ω–Ω—ã–µ —É—Å–ø–µ—à–Ω–æ –æ–±–Ω–æ–≤–ª–µ–Ω—ã!');
                setIsEditing(false);
            } else {
                setShowInformationModal(true);
                setErrorModal(true);
                setModalMessage('–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö');
            }
        } catch (error) {
            console.error('–û—à–∏–±–∫–∞:', error);
            setShowInformationModal(true);
            setErrorModal(true);
            setModalMessage('–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö');
        }
    };

    return (
        <div className='why__container'>
            <h2 className='why__title'>–ü–æ—á–µ–º—É –Ω–∞—Å –≤—ã–±–∏—Ä–∞—é—Ç?</h2>

            {isLoggedIn && !isEditing && (
                <button className='why__edit-button' onClick={() => setIsEditing(true)}>‚úèÔ∏è –í–Ω–µ—Å—Ç–∏ –¥–∞–Ω–Ω—ã–µ</button>
            )}

            <div className='why__blocks-wrapper'>
                {data.map((reason, index) => (
                    <div className='why__block' key={reason.id || index}>
                        <div className='why__image-content'>
                            {/* –ò—Å–ø–æ–ª—å–∑—É–µ–º –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è, –ø–æ–ª—É—á–µ–Ω–Ω—ã–µ —Å —Å–µ—Ä–≤–µ—Ä–∞ –∏–ª–∏ –∏–∑ –¥–µ—Ñ–æ–ª—Ç–Ω–æ–≥–æ –º–∞—Å—Å–∏–≤–∞ */}
                            <img className='why__image' src={reason.image} alt={reason.alt || '–ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ'} />
                        </div>
                        <div className='why__text-content'>
                            {isEditing ? (
                                <>
                                    <textarea
                                        className='why__block-title-edit'
                                        value={reason.title_block}
                                        onChange={(e) => handleChange(index, 'title_block', e.target.value)}
                                    />
                                    <textarea
                                        className='why__block-text-edit'
                                        value={reason.block_descr}
                                        onChange={(e) => handleChange(index, 'block_descr', e.target.value)}
                                    />
                                </>
                            ) : (
                                <>
                                    <h4 className='why__block-title'>{reason.title_block}</h4>
                                    <p className='why__block-text'>{reason.block_descr}</p>
                                </>
                            )}
                        </div>
                        <div className='why__number-block'>{index + 1}</div>
                    </div>
                ))}
            </div>

            {isEditing && (
                <button className='why__save-button' onClick={saveData}>üíæ –°–æ—Ö—Ä–∞–Ω–∏—Ç—å</button>
            )}

            {showInformationModal && (<InformationModal isError={errorModal} modalMessage={modalMessage} onClose={() => setShowInformationModal(false)}/>)}
        </div>
    );
}
